 1→import SwiftUI
 2→
 3→struct ContentView: View {
 4→    @ObservedObject var viewModel: DocumentViewModel
 5→
 6→    var body: some View {
 7→        NavigationSplitView(columnVisibility: sidebarBinding) {
 8→            SidebarView(viewModel: viewModel)
 9→        } detail: {
10→            detailContent
11→                .frame(maxWidth: .infinity, maxHeight: .infinity)
12→                .background(Tokens.Colors.background)
13→        }
14→        .navigationSplitViewStyle(.prominentDetail)
15→        .toolbar {
16→            ToolbarItems(viewModel: viewModel)
17→        }
18→    }
19→
20→    @ViewBuilder
21→    private var detailContent: some View {
22→        if let error = viewModel.error, viewModel.document == nil || error == .fileDeleted {
23→            ErrorStateView(error: error)
24→        } else if let document = viewModel.document {
25→            MarkdownContentView(content: document.content)
26→        } else {
27→            EmptyStateView()
28→        }
29→    }
30→
31→    private var sidebarBinding: Binding<NavigationSplitViewVisibility> {
32→        Binding(
33→            get: { viewModel.sidebarVisible ? .doubleColumn : .detailOnly },
34→            set: { newValue in
35→                viewModel.sidebarVisible = newValue != .detailOnly
36→            }
37→        )
38→    }
39→}
