41→// MARK: - Error state
42→
43→private struct ErrorStateView: View {
44→    let error: SafoError
45→
46→    var body: some View {
47→        VStack(spacing: Tokens.Spacing.errorStateGap) {
48→            Text(title)
49→                .font(.system(size: Tokens.Typography.bodySize, weight: .medium))
50→                .foregroundStyle(Tokens.Colors.textSecondary)
51→            if let detail {
52→                Text(detail)
53→                    .font(.system(size: Tokens.Typography.codeSize))
54→                    .foregroundStyle(Tokens.Colors.textTertiary)
55→            }
56→        }
57→    }
58→
59→    private var title: String {
60→        switch error {
61→        case .fileNotFound:
62→            return "File not found"
63→        case .notMarkdownFile:
64→            return "Safo only opens markdown files"
65→        case .permissionDenied:
66→            return "Cannot read file: permission denied"
67→        case .emptyFile:
68→            return "Empty file"
69→        case .fileDeleted:
70→            return "File was deleted"
71→        }
72→    }
73→
74→    private var detail: String? {
75→        switch error {
76→        case .fileNotFound(let path):
77→            return path
78→        case .notMarkdownFile(let path):
79→            return path
80→        case .permissionDenied(let path):
81→            return path
82→        case .emptyFile, .fileDeleted:
83→            return nil
84→        }
85→    }
86→}
87→
88→// MARK: - Empty state (no file opened)
89→
90→private struct EmptyStateView: View {
91→    var body: some View {
92→        Text("Open a markdown file to get started")
93→            .font(.system(size: Tokens.Typography.bodySize))
94→            .foregroundStyle(Tokens.Colors.textTertiary)
95→    }
96→}
