 1→import AppKit
 2→
 3→final class AppDelegate: NSObject, NSApplicationDelegate {
 4→    func applicationDidFinishLaunching(_ notification: Notification) {
 5→        DispatchQueue.main.async {
 6→            self.positionWindow()
 7→        }
 8→    }
 9→
10→    private func positionWindow() {
11→        guard let screen = NSScreen.main else { return }
12→        guard let window = NSApplication.shared.windows.first else { return }
13→
14→        let screenFrame = screen.visibleFrame
15→        let width = screenFrame.width * Tokens.Layout.windowWidthRatio
16→
17→        let frame = NSRect(
18→            x: screenFrame.maxX - width,
19→            y: screenFrame.minY,
20→            width: width,
21→            height: screenFrame.height
22→        )
23→
24→        window.setFrame(frame, display: true, animate: false)
25→    }
26→}
