19→            } label: {
20→                Image(systemName: "chevron.left")
21→            }
22→            .disabled(!(viewModel.directoryListing?.hasPrevious ?? false))
23→            .keyboardShortcut("[", modifiers: .command)
24→
25→            Button {
26→                viewModel.navigateToNext()
27→            } label: {
28→                Image(systemName: "chevron.right")
29→            }
30→            .disabled(!(viewModel.directoryListing?.hasNext ?? false))
31→            .keyboardShortcut("]", modifiers: .command)
32→        }
33→
34→        ToolbarItem(placement: .principal) {
35→            if let document = viewModel.document {
36→                VStack(spacing: 1) {
