 1→import SwiftUI
 2→
 3→struct SidebarView: View {
 4→    @ObservedObject var viewModel: DocumentViewModel
 5→
 6→    var body: some View {
 7→        List {
 8→            if let listing = viewModel.directoryListing {
 9→                let grouped = groupedFiles(listing: listing)
10→
11→                if !grouped.rootFiles.isEmpty {
12→                    ForEach(grouped.rootFiles, id: \.self) { url in
13→                        fileRow(url: url, listing: listing)
14→                    }
15→                }
16→
17→                ForEach(grouped.folders, id: \.name) { folder in
18→                    Section(folder.name) {
19→                        ForEach(folder.files, id: \.self) { url in
20→                            fileRow(url: url, listing: listing)
21→                        }
22→                    }
23→                }
24→            }
25→        }
26→        .listStyle(.sidebar)
27→        .frame(minWidth: Tokens.Layout.sidebarWidth, idealWidth: Tokens.Layout.sidebarWidth)
28→    }
29→
30→    private func fileRow(url: URL, listing: DirectoryListing) -> some View {
31→        let isCurrent = url == listing.currentFile
32→
33→        return Button {
34→            viewModel.navigateTo(url: url)
35→        } label: {
36→            Text(url.lastPathComponent)
37→                .font(.system(size: Tokens.Typography.sidebarSize))
38→                .foregroundStyle(isCurrent ? Tokens.Colors.accent : Tokens.Colors.textPrimary)
39→                .frame(maxWidth: .infinity, alignment: .leading)
40→        }
41→        .buttonStyle(.plain)
42→        .listRowBackground(
43→            isCurrent ? Tokens.Colors.accent.opacity(0.1) : Color.clear
44→        )
45→    }
46→}
47→
48→// MARK: - File grouping
49→
50→private extension SidebarView {
51→    struct FolderGroup: Equatable {
52→        let name: String
53→        let files: [URL]
54→    }
55→
56→    struct GroupedFiles {
57→        let rootFiles: [URL]
58→        let folders: [FolderGroup]
59→    }
60→
61→    func groupedFiles(listing: DirectoryListing) -> GroupedFiles {
62→        var rootFiles: [URL] = []
63→        var folderMap: [String: [URL]] = [:]
64→
65→        let rootPath = listing.rootURL.path
66→
67→        for file in listing.files {
68→            let filePath = file.path
69→            let relativePath: String
70→            if filePath.hasPrefix(rootPath) {
71→                let stripped = String(filePath.dropFirst(rootPath.count))
72→                relativePath = stripped.hasPrefix("/") ? String(stripped.dropFirst()) : stripped
73→            } else {
74→                relativePath = file.lastPathComponent
75→            }
76→            let components = relativePath.split(separator: "/")
77→
78→            if components.count == 1 {
79→                rootFiles.append(file)
80→            } else {
81→                let folderName = String(components[0])
82→                folderMap[folderName, default: []].append(file)
83→            }
84→        }
85→
86→        let folders = folderMap.keys.sorted().map { name in
87→            FolderGroup(name: name, files: folderMap[name] ?? [])
88→        }
89→
90→        return GroupedFiles(rootFiles: rootFiles, folders: folders)
91→    }
92→}
