  1→import AppKit
  2→import Foundation
  3→import SwiftUI
  4→
  5→@MainActor
  6→final class DocumentViewModel: ObservableObject {
  7→    @Published var document: MarkdownDocument?
  8→    @Published var directoryListing: DirectoryListing?
  9→    @Published var sidebarVisible = false
 10→    @Published var error: SafoError?
 11→
 12→    private var fileWatcher: FileWatcher?
 13→
 14→    // MARK: - Navigation
 15→
 16→    func open(url: URL) {
 17→        loadFile(url: url, rescanDirectory: true)
 18→    }
 19→
 20→    func navigateToNext() {
 21→        guard var listing = directoryListing, listing.hasNext else { return }
 22→        listing.goToNext()
 23→        directoryListing = listing
 24→        if let file = listing.currentFile {
 25→            loadFile(url: file, rescanDirectory: false)
 26→        }
 27→    }
 28→
 29→    func navigateToPrevious() {
 30→        guard var listing = directoryListing, listing.hasPrevious else { return }
 31→        listing.goToPrevious()
 32→        directoryListing = listing
 33→        if let file = listing.currentFile {
 34→            loadFile(url: file, rescanDirectory: false)
 35→        }
 36→    }
 37→
 38→    func navigateTo(url: URL) {
 39→        guard var listing = directoryListing else {
 40→            open(url: url)
 41→            return
 42→        }
 43→        listing.goTo(url: url)
 44→        directoryListing = listing
 45→        loadFile(url: url, rescanDirectory: false)
 46→    }
 47→
 48→    // MARK: - Actions
 49→
 50→    func copyToClipboard() {
 51→        guard let content = document?.content else { return }
 52→        NSPasteboard.general.clearContents()
 53→        NSPasteboard.general.setString(content, forType: .string)
 54→    }
 55→
 56→    func toggleSidebar() {
 57→        withAnimation(Tokens.Animation.sidebar) {
 58→            sidebarVisible.toggle()
 59→        }
 60→    }
 61→
 62→    // MARK: - Private
 63→
 64→    private func loadFile(url: URL, rescanDirectory: Bool) {
 65→        error = nil
 66→        fileWatcher?.stop()
 67→        fileWatcher = nil
 68→
 69→        do {
 70→            let doc = try MarkdownDocument.load(from: url)
 71→
 72→            if doc.content.isEmpty {
 73→                error = .emptyFile
 74→            }
 75→
 76→            document = doc
 77→
 78→            if rescanDirectory {
 79→                let directory = url.deletingLastPathComponent()
 80→                directoryListing = DirectoryListing.scan(directory: directory, currentFile: url.standardizedFileURL)
 81→            }
 82→
 83→            fileWatcher = FileWatcher(url: url) { [weak self] in
 84→                self?.reloadCurrentFile()
 85→            }
 86→        } catch let safoError as SafoError {
 87→            error = safoError
 88→            document = nil
 89→        } catch {
 90→            self.error = .fileNotFound(url.path)
 91→            document = nil
 92→        }
 93→    }
 94→
 95→    private func reloadCurrentFile() {
 96→        guard let currentURL = document?.url else { return }
 97→
 98→        guard FileManager.default.fileExists(atPath: currentURL.path) else {
 99→            error = .fileDeleted
100→            fileWatcher?.stop()
101→            fileWatcher = nil
102→            return
103→        }
104→
105→        do {
106→            let doc = try MarkdownDocument.load(from: currentURL)
107→            document = doc
108→            error = doc.content.isEmpty ? .emptyFile : nil
109→        } catch {
110→            // File may be mid-write; ignore transient errors during reload
111→        }
112→    }
113→}
114→

 1→import Foundation
 2→
 3→struct MarkdownDocument: Identifiable, Equatable {
 4→    let id: URL
 5→    let url: URL
 6→    var content: String
 7→    var lastModified: Date
 8→
 9→    var fileName: String {
10→        url.lastPathComponent
11→    }
12→
13→    func relativePath(to rootURL: URL) -> String {
14→        let filePath = url.path
15→        let rootPath = rootURL.path
16→        if filePath.hasPrefix(rootPath) {
17→            let relative = String(filePath.dropFirst(rootPath.count))
18→            return relative.hasPrefix("/") ? String(relative.dropFirst()) : relative
19→        }
20→        return fileName
21→    }
22→
23→    static func load(from url: URL) throws -> MarkdownDocument {
24→        let fileURL = url.standardizedFileURL
25→
26→        guard FileManager.default.fileExists(atPath: fileURL.path) else {
27→            throw SafoError.fileNotFound(fileURL.path)
28→        }
29→
30→        let ext = fileURL.pathExtension.lowercased()
31→        guard ext == "md" || ext == "markdown" else {
32→            throw SafoError.notMarkdownFile(fileURL.path)
33→        }
34→
35→        let content: String
36→        do {
37→            content = try String(contentsOf: fileURL, encoding: .utf8)
38→        } catch let error as NSError where error.domain == NSCocoaErrorDomain && error.code == NSFileReadNoPermissionError {
39→            throw SafoError.permissionDenied(fileURL.path)
40→        }
41→
42→        let lastModified = (try? FileManager.default.attributesOfItem(atPath: fileURL.path))?[.modificationDate] as? Date ?? Date()
43→
44→        return MarkdownDocument(
45→            id: fileURL,
46→            url: fileURL,
47→            content: content,
48→            lastModified: lastModified
49→        )
50→    }
51→}
