 1→import Foundation
 2→
 3→struct DirectoryListing: Equatable {
 4→    let rootURL: URL
 5→    var files: [URL]
 6→    var currentIndex: Int
 7→
 8→    var currentFile: URL? {
 9→        guard files.indices.contains(currentIndex) else { return nil }
10→        return files[currentIndex]
11→    }
12→
13→    var hasPrevious: Bool {
14→        currentIndex > 0
15→    }
16→
17→    var hasNext: Bool {
18→        currentIndex < files.count - 1
19→    }
20→
21→    mutating func goToNext() {
22→        guard hasNext else { return }
23→        currentIndex += 1
24→    }
25→
26→    mutating func goToPrevious() {
27→        guard hasPrevious else { return }
28→        currentIndex -= 1
29→    }
30→
31→    mutating func goTo(url: URL) {
32→        guard let index = files.firstIndex(of: url) else { return }
33→        currentIndex = index
34→    }
35→
36→    static func scan(directory: URL, currentFile: URL? = nil) -> DirectoryListing {
37→        let fm = FileManager.default
38→        guard let enumerator = fm.enumerator(
39→            at: directory,
40→            includingPropertiesForKeys: [.isRegularFileKey],
41→            options: [.skipsHiddenFiles]
42→        ) else {
43→            return DirectoryListing(rootURL: directory, files: [], currentIndex: 0)
44→        }
45→
46→        var files: [URL] = []
47→        for case let fileURL as URL in enumerator {
48→            let ext = fileURL.pathExtension.lowercased()
49→            if ext == "md" || ext == "markdown" {
50→                files.append(fileURL)
51→            }
52→        }
53→
54→        files.sort { $0.path < $1.path }
55→
56→        let index: Int
57→        if let currentFile, let found = files.firstIndex(of: currentFile) {
58→            index = found
59→        } else {
60→            index = 0
61→        }
62→
63→        return DirectoryListing(rootURL: directory, files: files, currentIndex: index)
64→    }
65→}
