 23→        }
 24→        .link {
 25→            ForegroundColor(Tokens.Colors.accent)
 26→        }
 27→        .heading1 { configuration in
 28→            configuration.label
 29→                .relativeLineSpacing(.em(0.125))
 30→                .markdownMargin(top: Tokens.Spacing.headingTopSpacing, bottom: Tokens.Typography.h1BottomSpacing)
 31→                .markdownTextStyle {
 32→                    FontWeight(Tokens.Typography.h1Weight)
 33→                    FontSize(Tokens.Typography.h1Size)
 34→                    TextTracking(Tokens.Typography.h1Tracking)
 35→                }
 36→        }
