  4→
  5→// MARK: - Splash theme definitions
  6→
  7→enum CodeTheme {
  8→    static let dark = Splash.Theme(
  9→        font: Splash.Font(size: Double(Tokens.Typography.codeSize)),
 10→        plainTextColor: NSColor(Tokens.CodeColors.darkPlain),
 11→        tokenColors: [
 12→            .keyword: NSColor(Tokens.CodeColors.darkKeyword),
 13→            .string: NSColor(Tokens.CodeColors.darkString),
 14→            .type: NSColor(Tokens.CodeColors.darkType),
 15→            .call: NSColor(Tokens.CodeColors.darkCall),
 16→            .number: NSColor(Tokens.CodeColors.darkNumber),
 17→            .comment: NSColor(Tokens.CodeColors.darkComment),
 18→            .property: NSColor(Tokens.CodeColors.darkProperty),
 19→            .dotAccess: NSColor(Tokens.CodeColors.darkProperty),
 20→            .preprocessing: NSColor(Tokens.CodeColors.darkKeyword),
 21→        ],
 22→        backgroundColor: NSColor(Tokens.CodeColors.darkBackground)
 23→    )
 24→
 25→    static let light = Splash.Theme(
 26→        font: Splash.Font(size: Double(Tokens.Typography.codeSize)),
 27→        plainTextColor: NSColor(Tokens.CodeColors.lightPlain),
 28→        tokenColors: [
 29→            .keyword: NSColor(Tokens.CodeColors.lightKeyword),
 30→            .string: NSColor(Tokens.CodeColors.lightString),
 31→            .type: NSColor(Tokens.CodeColors.lightType),
 32→            .call: NSColor(Tokens.CodeColors.lightCall),
 33→            .number: NSColor(Tokens.CodeColors.lightNumber),
 34→            .comment: NSColor(Tokens.CodeColors.lightComment),
 35→            .property: NSColor(Tokens.CodeColors.lightProperty),
 36→            .dotAccess: NSColor(Tokens.CodeColors.lightProperty),
 37→            .preprocessing: NSColor(Tokens.CodeColors.lightKeyword),
 38→        ],
 39→        backgroundColor: NSColor(Tokens.CodeColors.lightBackground)
 40→    )
 41→}
 42→
 43→// MARK: - Splash to SwiftUI Text output format
 44→
 45→private struct TextOutputFormat: OutputFormat {
 46→    private let theme: Splash.Theme
 47→
 48→    init(theme: Splash.Theme) {
 49→        self.theme = theme
 50→    }
 51→
 52→    func makeBuilder() -> Builder {
 53→        Builder(theme: theme)
 54→    }
 55→}
 56→
 57→extension TextOutputFormat {
 58→    struct Builder: OutputBuilder {
 59→        private let theme: Splash.Theme
 60→        private var accumulatedText: [Text]
 61→
 62→        fileprivate init(theme: Splash.Theme) {
 63→            self.theme = theme
 64→            self.accumulatedText = []
 65→        }
 66→
 67→        mutating func addToken(_ token: String, ofType type: TokenType) {
 68→            let color = theme.tokenColors[type] ?? theme.plainTextColor
 69→            accumulatedText.append(Text(token).foregroundColor(SwiftUI.Color(nsColor: color)))
 70→        }
 71→
 72→        mutating func addPlainText(_ text: String) {
 73→            accumulatedText.append(
 74→                Text(text).foregroundColor(SwiftUI.Color(nsColor: theme.plainTextColor))
 75→            )
 76→        }
 77→
 78→        mutating func addWhitespace(_ whitespace: String) {
 79→            accumulatedText.append(Text(whitespace))
 80→        }
 81→
 82→        func build() -> Text {
 83→            accumulatedText.reduce(Text(""), +)
 84→        }
 85→    }
 86→}
 87→
 88→// MARK: - MarkdownUI code syntax highlighter
 89→
 90→struct SplashCodeHighlighter: CodeSyntaxHighlighter {
 91→    private let syntaxHighlighter: SyntaxHighlighter<TextOutputFormat>
 92→
 93→    init(theme: Splash.Theme) {
 94→        self.syntaxHighlighter = SyntaxHighlighter(format: TextOutputFormat(theme: theme))
 95→    }
 96→
 97→    func highlightCode(_ code: String, language: String?) -> Text {
 98→        guard language != nil else {
 99→            return Text(code)
100→        }
101→        return syntaxHighlighter.highlight(code)
102→    }
103→}
104→
105→extension CodeSyntaxHighlighter where Self == SplashCodeHighlighter {
106→    static func splash(theme: Splash.Theme) -> Self {
107→        SplashCodeHighlighter(theme: theme)
108→    }
109→}
