 1→import Foundation
 2→
 3→final class FileWatcher {
 4→    private let url: URL
 5→    private let onChange: () -> Void
 6→    private var source: DispatchSourceFileSystemObject?
 7→    private var fileDescriptor: Int32 = -1
 8→    private var debounceWorkItem: DispatchWorkItem?
 9→
10→    private static let debounceInterval: TimeInterval = 0.1
11→
12→    init(url: URL, onChange: @escaping () -> Void) {
13→        self.url = url
14→        self.onChange = onChange
15→        startWatching()
16→    }
17→
18→    deinit {
19→        stop()
20→    }
21→
22→    func stop() {
23→        debounceWorkItem?.cancel()
24→        debounceWorkItem = nil
25→        source?.cancel()
26→        source = nil
27→    }
28→
29→    // MARK: - Private
30→
31→    private func startWatching() {
32→        let fd = open(url.path, O_EVTONLY)
33→        guard fd >= 0 else { return }
34→        fileDescriptor = fd
35→
36→        let source = DispatchSource.makeFileSystemObjectSource(
37→            fileDescriptor: fd,
38→            eventMask: [.write, .rename, .delete],
39→            queue: .main
40→        )
41→
42→        source.setEventHandler { [weak self] in
43→            guard let self else { return }
44→            let flags = source.data
45→            if flags.contains(.rename) || flags.contains(.delete) {
46→                self.handleRenameOrDelete()
47→            } else {
48→                self.debouncedOnChange()
49→            }
50→        }
51→
52→        source.setCancelHandler { [fd] in
53→            close(fd)
54→        }
55→
56→        source.resume()
57→        self.source = source
58→    }
59→
60→    private func handleRenameOrDelete() {
61→        stop()
62→
63→        // Editors like vim write by creating a new file and renaming.
64→        // Wait briefly then re-watch if the file reappears at the same path.
65→        DispatchQueue.main.asyncAfter(deadline: .now() + Self.debounceInterval) { [weak self] in
66→            guard let self else { return }
67→            if FileManager.default.fileExists(atPath: self.url.path) {
68→                self.startWatching()
69→                self.onChange()
70→            } else {
71→                self.onChange()
72→            }
73→        }
74→    }
75→
76→    private func debouncedOnChange() {
77→        debounceWorkItem?.cancel()
78→        let workItem = DispatchWorkItem { [weak self] in
79→            self?.onChange()
80→        }
81→        debounceWorkItem = workItem
82→        DispatchQueue.main.asyncAfter(deadline: .now() + Self.debounceInterval, execute: workItem)
83→    }
84→}
