 3→@main
 4→struct SafoApp: App {
 5→    @NSApplicationDelegateAdaptor(AppDelegate.self) var appDelegate
 6→    @StateObject private var viewModel = DocumentViewModel()
 7→
 8→    var body: some Scene {
 9→        WindowGroup {
10→            ContentView(viewModel: viewModel)
11→                .onOpenURL { url in
12→                    handleOpenURL(url)
13→                }
14→                .task {
15→                    openFromLaunchArguments()
16→                }
17→        }
18→        .handlesExternalEvents(matching: Set(arrayLiteral: "*"))
19→    }
20→
21→    private func handleOpenURL(_ url: URL) {
22→        guard let components = URLComponents(url: url, resolvingAgainstBaseURL: false),
23→              let path = components.queryItems?.first(where: { $0.name == "path" })?.value
24→        else { return }
25→
26→        let fileURL = URL(fileURLWithPath: path)
27→        viewModel.open(url: fileURL)
28→    }
29→
30→    private func openFromLaunchArguments() {
31→        let args = CommandLine.arguments
32→        guard args.count > 1 else { return }
33→
34→        let path = args[1]
35→        let url = URL(fileURLWithPath: path)
36→        viewModel.open(url: url)
37→    }
38→}
