 1→import Foundation
 2→
 3→@main
 4→struct SafoCLI {
 5→    static func main() {
 6→        guard CommandLine.arguments.count > 1 else {
 7→            printUsage()
 8→            exit(1)
 9→        }
10→
11→        let rawPath = CommandLine.arguments[1]
12→        let absolutePath = resolveAbsolutePath(rawPath)
13→
14→        guard FileManager.default.fileExists(atPath: absolutePath) else {
15→            printError("file not found: \(absolutePath)")
16→            exit(1)
17→        }
18→
19→        guard absolutePath.hasSuffix(".md") || absolutePath.hasSuffix(".markdown") else {
20→            printError("not a markdown file: \(absolutePath)")
21→            exit(1)
22→        }
23→
24→        guard let encodedPath = absolutePath.addingPercentEncoding(withAllowedCharacters: .urlQueryAllowed) else {
25→            printError("could not encode path: \(absolutePath)")
26→            exit(1)
27→        }
28→
29→        let urlString = "safo://open?path=\(encodedPath)"
30→
31→        let process = Process()
32→        process.executableURL = URL(fileURLWithPath: "/usr/bin/open")
33→        process.arguments = [urlString]
34→
35→        do {
36→            try process.run()
37→            process.waitUntilExit()
38→        } catch {
39→            printError("could not open Safo: \(error.localizedDescription)")
40→            exit(1)
41→        }
42→    }
43→
44→    private static func resolveAbsolutePath(_ rawPath: String) -> String {
45→        if rawPath.hasPrefix("/") {
46→            return rawPath
47→        }
48→
49→        if rawPath.hasPrefix("~") {
50→            return NSString(string: rawPath).expandingTildeInPath
51→        }
52→
53→        let cwd = FileManager.default.currentDirectoryPath
54→        let url = URL(fileURLWithPath: cwd).appendingPathComponent(rawPath)
55→        return url.standardized.path
56→    }
57→
58→    private static func printUsage() {
59→        let message = """
60→        Usage: safo <file.md>
61→
62→        Open a markdown file in Safo for preview.
63→        """
64→        FileHandle.standardError.write(Data(message.utf8))
65→        FileHandle.standardError.write(Data("\n".utf8))
66→    }
67→
68→    private static func printError(_ message: String) {
69→        let output = "safo: \(message)\n"
70→        FileHandle.standardError.write(Data(output.utf8))
71→    }
72→}
