 1→import Foundation
 2→
 3→struct MarkdownDocument: Identifiable, Equatable {
 4→    let id: URL
 5→    let url: URL
 6→    var content: String
 7→    var lastModified: Date
 8→
 9→    var fileName: String {
10→        url.lastPathComponent
11→    }
12→
13→    func relativePath(to rootURL: URL) -> String {
14→        let filePath = url.path
15→        let rootPath = rootURL.path
16→        if filePath.hasPrefix(rootPath) {
17→            let relative = String(filePath.dropFirst(rootPath.count))
18→            return relative.hasPrefix("/") ? String(relative.dropFirst()) : relative
19→        }
20→        return fileName
21→    }
22→
23→    static func load(from url: URL) throws -> MarkdownDocument {
24→        let fileURL = url.standardizedFileURL
25→
26→        guard FileManager.default.fileExists(atPath: fileURL.path) else {
27→            throw SafoError.fileNotFound(fileURL.path)
28→        }
29→
30→        let ext = fileURL.pathExtension.lowercased()
31→        guard ext == "md" || ext == "markdown" else {
32→            throw SafoError.notMarkdownFile(fileURL.path)
33→        }
34→
35→        let content: String
36→        do {
37→            content = try String(contentsOf: fileURL, encoding: .utf8)
38→        } catch let error as NSError where error.domain == NSCocoaErrorDomain && error.code == NSFileReadNoPermissionError {
39→            throw SafoError.permissionDenied(fileURL.path)
40→        }
41→
42→        let lastModified = (try? FileManager.default.attributesOfItem(atPath: fileURL.path))?[.modificationDate] as? Date ?? Date()
43→
44→        return MarkdownDocument(
45→            id: fileURL,
46→            url: fileURL,
47→            content: content,
48→            lastModified: lastModified
49→        )
50→    }
51→}
